
public class FloatingLiterals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            System.out.println(500.00f);//Float Literal
            System.out.println(500.00);// Double Literal
            
            System.out.println(1.0E20f);//Float Literal
            System.out.println(1.0e200);//Double Literal
            
            System.out.println(1.0E-3);
	}

}
