
public class Main1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int v1=100;
        int v2=200;
        int v3=v1+v2;
        System.out.println(v3);
        
        int v4=v2-v1;
        System.out.println(v4);
        
        int v5=v1*v2;
        System.out.println(v5);
        
        int v6=v2/v1;
        System.out.println(v6);
        
        int v7=v1%v2;
        System.out.println(v7);
        
        float v8=9.0f;
              v8+=10.0f;
        System.out.println(v8);
        
        v8-=10.0f;
        System.out.println(v8);
        
        v8*=10.0f;
        System.out.println(v8);
        
        v8/=10.0f;
        System.out.println(v8);
        
        v8%=10.0f;
        System.out.println(v8);
        
        byte v9=100;
        v9++;
        System.out.println(v9);
        
        v9--;
        System.out.println(v9);
        
        --v9;
        System.out.println(v9);
        
        ++v9;
        System.out.println(v9);
        
        
	}

}
/*
 * operators re the symbols to perform calculation operations 
 * They are of 3 types 
 * Arithmetic -    +,-,*,/,%,+=,-=,*=,/=,%=,++,--
 * Relational -    <,>,<=,>=,==,!=
 * Logical    -    &&,||,&,|,!
 
*/