
public class Main2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int v1=10;
		int v2=10;
		int v3=100;
		
		boolean result1=(v1==v2);
		System.out.println(result1);
		
		boolean result2=(v1!=v2);
		System.out.println(result2);
		
		boolean result3=(v1<200);
		System.out.println(result3);
		
		boolean result4=(v1>200);
		System.out.println(result4);
		
		boolean result5=(v1<=200);
		System.out.println(result5);
		
		boolean result6=(v1>=200);
		System.out.println(result6);
		
		
				

	}

}
/*
 * operators re the symbols to perform calculation operations 
 * They are of 3 types 
 * Arithmetic -    +,-,*,/,%,+=,-=,*=,/=,%=,++,--
 * Relational -    <,>,<=,>=,==,!=
 * Logical    -    &&,||,&,|,!
 
*/